<?php // Poscast - file/uploadlib.php, 24/02/2007/** * Recupération du script d'upload de Moodle modifié pour stocker les  * fichiers multimédia et rss dans un dossier accessible par http. * Les fichiers muttimédia sont stockés dans le dossier mod/podcast/media * de Moodle (Ex: mod/podcast/media/'courseid'/'podcastid'/'nomfichier').  */    require_once("../../config.php");    require_once("lib.php");    $id = required_param('id', PARAM_INT);   // course    if (! $course = get_record("course", "id", $id)) {        error("Course ID is incorrect");    }    require_login($course->id);    add_to_log($course->id, "podcast", "view all", "index.php?id=$course->id", "");	// Get all required strings    $strpodcasts = get_string("modulenameplural", "podcast");    $strpodcast  = get_string("modulename", "podcast");	// Print the header    if ($course->category) {        $navigation = "<a href=\"../../course/view.php?id=$course->id\">$course->shortname</a> ->";    } else {        $navigation = '';    }    print_header("$course->shortname: $strpodcasts", "$course->fullname", "$navigation $strpodcasts", "", "", true, "", navmenu($course));	// Get all the appropriate data    if (! $podcasts = get_all_instances_in_course("podcast", $course)) {        notice("There are no podcasts", "../../course/view.php?id=$course->id");        die;    }	// Print the list of instances (your module will probably extend this)    $timenow = time();    $strname  = get_string("name");    $strweek  = get_string("week");    $strtopic  = get_string("topic");    if ($course->format == "weeks") {        $table->head  = array ($strweek, $strname);        $table->align = array ("center", "left");    } else if ($course->format == "topics") {        $table->head  = array ($strtopic, $strname);        $table->align = array ("center", "left", "left", "left");    } else {        $table->head  = array ($strname);        $table->align = array ("left", "left", "left");    }    foreach ($podcasts as $podcast) {        if (!$podcast->visible) {            //Show dimmed if the mod is hidden            $link = "<a class=\"dimmed\" href=\"view.php?id=$podcast->coursemodule\">$podcast->name</a>";        } else {            //Show normal if the mod is visible            $link = "<a href=\"view.php?id=$podcast->coursemodule\">$podcast->name</a>";        }        if ($course->format == "weeks" or $course->format == "topics") {            $table->data[] = array ($podcast->section, $link);        } else {            $table->data[] = array ($link);        }    }    echo "<br />";    print_table($table);	// Finish the page    print_footer($course);?>